package com.suogao.suogao_android.helper;import android.content.Context;import com.google.gson.Gson;import com.suogao.suogao_android.model.HomeModel;import com.suogao.suogao_android.model.TimeTreeModel;import com.suogao.suogao_android.util.TestUtil;import com.suogao.suogaoapp.R;/** * @author henzil * @version create time:2013-7-23_下午8:57:29 * @Description 解析menu */public class MenuHelper {	private Context context;	public MenuHelper(Context context) {		this.context = context;	}	public TimeTreeModel getTimeTreeModel() {		String json = TestUtil.readTextFileFromRawResourceId(context, R.raw.time_tree);		TimeTreeModel menuListModel = new Gson().fromJson(json, TimeTreeModel.class);		if (menuListModel != null) {			return menuListModel;		}		return null;	}	public HomeModel getHomeModel() {		String json = TestUtil.readTextFileFromRawResourceId(context, R.raw.home_list);		HomeModel menuListModel = new Gson().fromJson(json, HomeModel.class);		if (menuListModel != null) {			return menuListModel;		}		return null;	}	}